			function _findRemoveItem(strArray, item) {
				var tmp = _util.toCleanArray(strArray),
					idx = tmp.indexOf(item);
				if (idx > -1) {
					tmp.splice(idx, 1);
				}
				if (tmp[0] === "") {
					tmp.splice(0, 1);
				}
				return tmp.join();
			}

			function _addItem(strArray, item) {
				var tmp = _util.toCleanArray(strArray),
					idx = tmp.indexOf(item);
				if (idx > -1) {
					return strArray;
				}
				tmp.push(item);
				if (tmp[0] === "") {
					tmp.splice(0, 1);
				}
				return tmp.join();
			}

